import React, { useEffect, useState } from 'react'
import MainLayout from '../Layouts/MainLayout'
import axios from 'axios'

const POSPage = () => {

    const [products, setProducts] = useState([]);
    const [isloading, setIsLoading] = useState(false);
    const [cart, setCart] = useState([]);

    const fetcProducts = async () => {
        setIsLoading(true);
        const result = await axios.get('products');
        setProducts(await result.data);
        setIsLoading(false);
    }

    const addProductCart = async (product) => {
        // console.log(product);
        //check if we can add adding is exists
        let findProducctInCart = await cart.find(i => {
            returni.id === product.id
        });

        if (findProducctInCart) {

            let newCart = [];
            let newItem;

            cart.forEach(cartItem => {
                if (cartItem.id === product.id) {
                    newItem = {
                        ...cartItem,
                        quantity: cartItem.quantity + 1,
                        totalamount: cartItem.price * (cartIem.quantity + 1)
                    }
                    newCart.push(newItem);
                }
            })
        } else {
            let addingproduct = {
                ...product,
                'quantity': 1,
                'totalamount': product.price,
            }

            setCart([...cart, addingproduct]);
        }
    }



    useEffect(() => {
        fetcProducts();
    }, []);





    return (
        <div>
            <MainLayout>
                POS content
                <div className='row'>
                    <div className='col-lg-8'>
                        {isloading ? 'Loading' : <div className='row'>

                            {products.map((product, key) =>
                                <div key={key} className='col-lg-4'>
                                    <div className='border' onClick={() => addProductCart(product)}>
                                        <p>{product.name}</p>
                                        <img src={product.image} className='img-fluid' alt={product.name} />
                                        <p>${product.price}</p>
                                    </div>
                                </div>
                            )}

                        </div>}
                    </div>
                    <div className='col-lg-4'> </div>
                </div>
            </MainLayout>
        </div>
    )
}

export default POSPage
